# Purpose: swap mouse locus in mm39.fa.gz for humanized locus in new file mm39_humanizedHs754.fa.gz

# Usage: python makeHumanizedFasta.py mm39.fa mm39_humanizedHs754.fa

import sys

inMouseFasta = open(sys.argv[1],'rt')
outFasta = open(sys.argv[2],'wt')

Homology5prime = ""
Homology3prime = ""
mouseHs754 = ""
humanHs754 = ""

wildType = Homology5prime.upper() + mouseHs754.upper() + Homology3prime.upper()
humanized = Homology5prime.upper() + humanHs754.upper() + Homology3prime.upper()

chromStatus = 'no'
for line in inMouseFasta:
    if line[0] == '>':
        if line.strip() == '>chr13':
            chromStatus = 'yes'
            chrom13string = ''
            outFasta.write('>chr13'+'\n')
            
        elif line.strip() == '>chr14':
            replacedString = chrom13string.replace(wildType, humanized)
            #print(len(chrom13string))
            #print(len(replacedString))
            i = 0
            while i*50 < len(replacedString):
                outFasta.write(replacedString[(i*50):((i*50)+50)]+'\n')
                i += 1
            chromStatus = 'no'
            outFasta.write(line)
        else:
            chromStatus = 'no'
            outFasta.write(line)
            
    else:
        if chromStatus == 'no':
            outFasta.write(line)
        if chromStatus == 'yes':
            chrom13string = chrom13string + line.strip().upper()
        
inMouseFasta.close()
outFasta.close()
